definition.script:
pipeline {
	agent any
	parameters {
        string(
            name: "PROJECT_NAME",
            description: "Проект для развертывания"
        )
		booleanParam(
            name: "INSTALL_AUTH",
            defaultValue: true,
            description: "Нужно ли задеплоить tournaments-auth?"
        )
		booleanParam(
            name: "INSTALL_CC",
            defaultValue: true,
            description: "Нужно ли задеплоить tournaments-competition-core?"
        )
		booleanParam(
            name: "INSTALL_AL",
            defaultValue: true,
            description: "Нужно ли задеплоить tournaments-account-linker?"
        )
		booleanParam(
            name: "INSTALL_DISCORD",
            defaultValue: true,
            description: "Нужно ли задеплоить tournaments-discord?"
        )
        string(
            name: "ANSIBLE_EXTRA_KEYS",
            description: "Дополнительные ключи ansible"
        )
    }
	environment {
	    VAULT_PASS = credentials('ansible-vault-password')
		PREPARE_CMD = "chmod o-w /workspace/ansible && cd /workspace/ansible"
		BASE_ANSIBLE_PARAMS = "-i ../inventory/general -i ../inventory/${params.PROJECT_NAME}/hosts -i ../inventory/${params.PROJECT_NAME}/configuration"
		ANSIBLE_EXEC = "/root/.local/bin/ansible"
		ANSIBLE_EXEC_PLAYBOOK = "/root/.local/bin/ansible-playbook"
		DEPLOY_GROUP = "deploy_backs"
	}
	stages {
	    stage("Test connection") {
            steps {
                script {
                    withCredentials([string(credentialsId: 'ansible-vault-password', variable: 'VAULT_PASS')]) {
                      sh """
                        ${env.PREPARE_CMD} && \
                        ${env.ANSIBLE_EXEC} ${env.BASE_ANSIBLE_PARAMS} -m ping ${env.DEPLOY_GROUP} \
                        ${params.ANSIBLE_EXTRA_KEYS} --vault-password-file <(echo "$VAULT_PASS")
                        """
                    }
                }
            }
        }
        stage("Prepare installation") {
		    parallel {
                stage("Install auth") {
                    when {
                        expression { params.INSTALL_AUTH }
                    }
                    steps {
                        script {
                             withCredentials([string(credentialsId: 'ansible-vault-password', variable: 'VAULT_PASS')]) {
    						  sh """
    							${env.PREPARE_CMD} && \
    							${env.ANSIBLE_EXEC_PLAYBOOK} ${env.BASE_ANSIBLE_PARAMS} ./backs/deploy-auth.yml \
    							${params.ANSIBLE_EXTRA_KEYS} --vault-password-file <(echo "$VAULT_PASS")
    							"""
                             }
    					}
                    }
                }
                stage("Install Competition Core") {
                    when {
                        expression { params.INSTALL_CC }
                    }
                    steps {
                        script {
                             withCredentials([string(credentialsId: 'ansible-vault-password', variable: 'VAULT_PASS')]) {
    						  sh """
    							${env.PREPARE_CMD} && \
    							${env.ANSIBLE_EXEC_PLAYBOOK} ${env.BASE_ANSIBLE_PARAMS} ./backs/deploy-cc.yml \
    							${params.ANSIBLE_EXTRA_KEYS} --vault-password-file <(echo "$VAULT_PASS")
    							"""
                             }
    					}
                    }
                }
				stage("Install Account Linker") {
                    when {
                        expression { params.INSTALL_AL }
                    }
                    steps {
                        script {
                             withCredentials([string(credentialsId: 'ansible-vault-password', variable: 'VAULT_PASS')]) {
    						  sh """
    							${env.PREPARE_CMD} && \
    							${env.ANSIBLE_EXEC_PLAYBOOK} ${env.BASE_ANSIBLE_PARAMS} ./backs/deploy-al.yml \
    							${params.ANSIBLE_EXTRA_KEYS} --vault-password-file <(echo "$VAULT_PASS")
    							"""
                             }
    					}
                    }
                }
				stage("Install Discord") {
                    when {
                        expression { params.INSTALL_DISCORD }
                    }
                    steps {
                        script {
                             withCredentials([string(credentialsId: 'ansible-vault-password', variable: 'VAULT_PASS')]) {
    						  sh """
    							${env.PREPARE_CMD} && \
    							${env.ANSIBLE_EXEC_PLAYBOOK} ${env.BASE_ANSIBLE_PARAMS} ./backs/deploy-discord.yml \
    							${params.ANSIBLE_EXTRA_KEYS} --vault-password-file <(echo "$VAULT_PASS")
    							"""
                             }
    					}
                    }
                }
            }
		}		
	}
}
